let getQueryParams=()=>{let search_str=window.location.search.substring(1);if(!search_str)return{};let params=search_str.split("&"),query={};return params.forEach(param=>{let p=param.split("="),key=p[0],val=p[1];query[key]?(Array.isArray(query[key])||(query[key]=[query[key]]),query[key].push(val)):query[key]=val}),query};window.getQueryParams=getQueryParams,window.location.query=getQueryParams();